const mongoose = require('mongoose');

const menuSchema = new mongoose.Schema(
    {
        name: String,
        price: Number,
        image: {
            type: String,
            default:
                'data:image/png;base64,',
        },
        soldout: {
            type: Boolean,
            default: false,
        },
    },
    { versionKey: false },
);
const storeSchema = new mongoose.Schema(
    {
        _id: Number,
        name: String,
        icon: {
            type: String,
            default:
                'data:image/png;base64,',
        },
        location: String,
        openStatus: {
            type: Boolean,
            default: true,
        },
        waitingOrderCount: {
            type: Number,
            default: 0,
        },
    },
    { versionKey: false },
);

const userSchema = new mongoose.Schema(
    {
        loginId: String,
        name: String,
        password: String,
        phone: String,
        email: String,
        createdDate: Date,
        status: {
            type: Boolean,
            default: true,
        },
    },
    { versionKey: false },
);
const submenu = new mongoose.Schema(
    {
        menuId: String,
        quantity: Number,
    },
    { versionKey: false, _id: false },
);
const orderSchema = new mongoose.Schema(
    {
        userId: String,
        shopId: Number,
        items: [submenu],
        paymentMethod: String,
        waitingCount: Number,
        takeout: Boolean,
        totalPrice: Number,
        createdTime: Date,
    },
    { versionKey: false },
);

module.exports = { menuSchema, storeSchema, userSchema, orderSchema };
